import React,{useState, useEffect} from "react";
import BasicForm from "./form-logic";
import TopNavigation from "./navigation";
import Footer from "./footer";
class AppElements extends React.Component {
  render() {
    return (
      <>
        <TopNavigation />
        <Footer />
      </>
    );
  }
}

