import React from "react";
import Gradient from "javascript-color-gradient";


export const ColorArray = () => {
  const secondGradient = new Gradient();
  secondGradient.setGradient("#c41b12", "#e16214", "84a220", "607D8B");
  secondGradient.setMidpoint(12);
  const secondColorArr = secondGradient.getArray();
};

class GetColorArray extends React.Component {
  render() {
    return (
      <>
        {ColorArray.secondColorArr((color) => {
          return (
            <button
              className="button"
              style={{ backgroundColor: color }}
              key={color}
            />
          );
        })}
      </>
    );
  }
}


export default class BasicForm extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      guess: "Take a Guess",
      again: "Take Another Guess"
    };
  }
  render() {
    return (
      <>
        <section>
          <p>I'm thinking of a number. Can you guess it?</p>
        </section>
        <section>
          <form>
            <input type="text" />
            <input type="submit" value={this.state.guess} />
          </form>
        </section>
        <GetColorArray />
      </>
    );
  };
  
}
