import React from "react";
import Gradient from "javascript-color-gradient";





export default class BasicForm extends React.Component {
  constructor(props) {
    super(props);
    const ColorArray = () => {
      const secondGradient = new Gradient();
    
      secondGradient.setGradient("#c41b12", "#e16214", "84a220", "607D8B");
    
      secondGradient.setMidpoint(12);
    
      const secondColorArr = secondGradient.getArray();
    
      return (
        <>
          {secondColorArr.map((color) => {
          })}
        </>
      );
    };
    this.state = {
      guess: "Take a Guess",
      colors: ColorArray
    };
  }
  ButtonChange = () => {
    this.setState = ({      
      guess: "Take Another Guess"
    })
  }
  render() {
    return (
      <>
        <section>
          <p>I'm thinking of a number. Can you guess it?</p>
        </section>
        <form>
          <input type="text" name="guessing"/>
          <button onClick={this.ButtonChange}>{this.state.guess}</button>
        </form>
        <button
                className="button"
                style={{ backgroundColor: color }}
                key={color}
              />
      </>
    );
  };
  
}
