export default class BasicForm extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      guess: "Take a Guess",
      again: "Take Another Guess",
    };
  }
  render() {
    return (
        <section>
          <form>
            <input type="text" />
            <input
              type="submit"
              value={this.state.guess}
              onClick={this.Buttonchange}
            />
          </form>
        </section>
    );
  }
}
